        public void loadApprovedBookings(){
        
            try {
                    Connection connection = (Connection) DBConnection.con();
                    // Get venue (id = 3) vendors where admin has approved registration
                     
                    VendorController vc = new VendorController();
                    VendorDetails vd = new VendorDetails();
                    
                    vd.setUsername(username);
                    ResultSet rs1 = vc.readVendorDetails(vd);
                    int vendor_id = 0;
                    while(rs1.next()){
                        vendor_id = rs1.getInt(1);
                    }
                    
                    PreparedStatement st = connection.prepareStatement("SELECT booking_id, booking_date, event_type, special_request, user_id FROM booking_details WHERE booking_id IN (SELECT booking_id FROM booking_event_details WHERE status = '3' and vendor_id =?)");
                    st.setInt(1, vendor_id);
                    ResultSet rs = st.executeQuery();
                    //System.out.println("Ran till here");
                    while(rs.next()){
                      String booking_id = rs.getString(1);
                      String booking_date = rs.getString(2);
                      String event_type = rs.getString(3);
                      String special_request = rs.getString(4);
                      String user_id = rs.getString(5);
                      //System.out.println("Ran while");
                      
                      String tblData[] = {booking_id, booking_date, event_type, special_request, user_id};
                      DefaultTableModel tblModel = (DefaultTableModel)jTable1.getModel();
                      tblModel.addRow(tblData);
                    }
                    
                } catch (SQLException sqlException) {
                    sqlException.printStackTrace();
                }
        }
