    public void loadBookingRequests(){
        
            try {
                    Connection connection = (Connection) DBConnection.con();
                    // Get venue (id = 3) vendors where admin has approved registration
                    PreparedStatement st = connection.prepareStatement("Select b.booking_id, b.event_type, b.booking_date, b.special_request, bed.vendor_id, CASE WHEN bed.status = 1 THEN \"Waiting for approval\" ELSE CASE WHEN bed.status = 2 THEN \"Rejected\" ELSE CASE WHEN bed.status = 4 THEN \"Cancelation requested\" ELSE \"Approved\" END END END as status from booking_details b, booking_event_details bed where b.booking_id=bed.booking_id and bed.status = 1");
                    
                    ResultSet rs = st.executeQuery();
                    //System.out.println("Ran till here");
                    while(rs.next()){
                      //String customerName = rs.getString(1);
                      String booking_id = rs.getString(1);
                      String event_type = rs.getString(2);
                      String booking_date = rs.getString(3);
                      String special_request = rs.getString(4);
                      String vendor_id = rs.getString(5);
                      String status = rs.getString(6);
                      
                      String tblData[] = {booking_id, event_type, booking_date, special_request, vendor_id, status};
                      DefaultTableModel tblModel = (DefaultTableModel)jTblManageBooking.getModel();
                      tblModel.addRow(tblData);
                    }
                    
                } catch (SQLException sqlException) {
                    sqlException.printStackTrace();
                } 
        }
