    private void jBtnApproveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnApproveActionPerformed
         // TODO add your handling code here:
         int selectedRowIndex = jTblManageBooking.getSelectedRow();
         String status = "Approved";
        if (selectedRowIndex <0){
            JOptionPane.showMessageDialog(this, "Please select a request to approve.");
            return;
        }
        DefaultTableModel model = (DefaultTableModel) jTblManageBooking.getModel();
        String booking_id = (String) model.getValueAt(selectedRowIndex, 0);
        //System.out.println(booking_id);
        
        if(jTblManageBooking.getSelectedRowCount()==1){
            model.setValueAt(status, selectedRowIndex, 5);
            
        try {
                    String sql = "UPDATE booking_event_details SET status = '3' WHERE booking_id = ?";
                    
                    Connection conn = (Connection) DBConnection.con();
                    PreparedStatement st = conn.prepareStatement(sql);
                    st.setString (1, booking_id);
                    st.executeUpdate();
                    //st.setString(1, status);
                    
                } catch (SQLException sqlException) {
                    sqlException.printStackTrace();
                } 
        }
    }//GEN-LAST:event_jBtnApproveActionPerformed

    private void jBtnRejectActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnRejectActionPerformed
       // TODO add your handling code here:
        int selectedRowIndex = jTblManageBooking.getSelectedRow();
         String status = "Rejected";
        if (selectedRowIndex <0){
            JOptionPane.showMessageDialog(this, "Please select a request to reject.");
            return;
        }
        DefaultTableModel model = (DefaultTableModel) jTblManageBooking.getModel();
        String booking_id = (String) model.getValueAt(selectedRowIndex, 0);

        if(jTblManageBooking.getSelectedRowCount()==1){
            model.setValueAt(status, selectedRowIndex, 5);
           
            try {
                    String sql = "UPDATE booking_event_details SET status = '2' WHERE booking_id = ?";
                    
                    Connection conn = (Connection) DBConnection.con();
                    PreparedStatement st = conn.prepareStatement(sql);
                    st.setString (1, booking_id);
                    st.executeUpdate();
                    //st.setString(1, status);
                    
                } catch (SQLException sqlException) {
                    sqlException.printStackTrace();
                } 
        
        }
    }//GEN-LAST:event_jBtnRejectActionPerformed


